clear;
clc;

total_start = tic;


%% read in old data and create arrays
exp_data = readtable('Transformed_exp_data.xlsx');

Press = table2array(exp_data(:,2));  % partial pressure O2 in bar
Temp = table2array(exp_data(:,3))+273.15;  % K
x_exp = table2array(exp_data(:,4));  % mol fraciton Ba
dd = table2array(exp_data(:,5));  % delta of delta

%% read in new data and create arrays
exp_new_data = readtable('evan_new_data.xlsx');

Press_new = table2array(exp_new_data(:,6));  % partial pressure O2
Temp_new = table2array(exp_new_data(:,5));  % K
x_exp_new = table2array(exp_new_data(:,2));  % mol fraciton Ba
dd_new = table2array(exp_new_data(:,7));  % delta of delta

%% read in DFT data
dft_data = table2array(readtable('BSF_data_phase_shift.xlsx'));
dft_data_copy = dft_data;
rows = any(isnan(dft_data),2);
dft_data(rows,:) = [];

X = dft_data(:,2); % mol fract Ba
Y = dft_data(:,1); % delta
Z = dft_data(:,3)/8; % E eV per ABO3

%% read in DFT redox data
dft_data2 = table2array(readtable('BSF_data_phase_shift_redox.xlsx'));
dft_data_copy2 = dft_data;
rows2 = any(isnan(dft_data2),2);
dft_data2(rows2,:) = [];

X2 = dft_data2(:,2); % mol fract Ba
Y2 = dft_data2(:,1); % delta
Z2 = dft_data2(:,3)/8; % E eV per ABO3

%% read in DFT cubic data
dft_data_cube = table2array(readtable('BSF_data.xlsx'));
dft_data_copy_cube = dft_data_cube;
rows_cube = any(isnan(dft_data_cube),2);
dft_data_cube(rows_cube,:) = [];

Xcube = dft_data_cube(:,2); % mol fract Ba
Ycube = dft_data_cube(:,1); % delta
Zcube = dft_data_cube(:,3)/8; % E eV per ABO3

%% Heat map of DFT data

S = repmat(100,numel(X),1);
s = S(:);
S2 = repmat(100,numel(X2),1);
s2 = S2(:);

figure
scatter(X,Y,s,Z,'*','LineWidth',2)
cb = colorbar; 
box on
title('Ba_{x}Sr_{1-x}FeO_{3-\delta}')
ylim([0 0.5])
ylabel('\delta')
xlabel('x')
zlabel('E (0 K)')
colormap jet
cb.Label.String = 'E(0K)';

figure
scatter(X2,Y2,s2,Z2,'*','LineWidth',2)
cb = colorbar; 
box on
title('Ba_{x}Sr_{1-x}FeO_{3-\delta}')
ylim([0 0.5])
ylabel('\delta')
xlabel('x')
zlabel('E (0 K)')
colormap jet
cb.Label.String = 'Reduciton Energy per \deltaO';


%% ENDMEBER CUBIC VS ORTHO

line0 = dft_data(dft_data(:,2)==0,:);
line1 = dft_data(dft_data(:,2)==1.0,:);

line00 = dft_data_cube(dft_data_cube(:,2)==0,:);
line11 = dft_data_cube(dft_data_cube(:,2)==1.0,:);

f0 = fit(line0(:,1),line0(:,3)/8,'poly3');
f1 = fit(line1(:,1),line1(:,3)/8,'poly3');

f00 = fit(line00(:,1),line00(:,3)/8,'poly3');
f11 = fit(line11(:,1),line11(:,3)/8,'poly3');

figure()
hold on
scatter(line00(:,1),line00(:,3)/8,'*k')
scatter(line0(:,1),line0(:,3)/8,'dr')
plot(f0,'--r')
plot(f00,'--k')
legend('No Phase Shift','With Phase Shift')
title('SrFeO_{3-\delta}')
ylim([-69, -67.5])
ylabel('DFT E(0K) (eV)')
xlabel('\delta')
hold off 

figure()
hold on
scatter(line11(:,1),line11(:,3)/8,'*k')
scatter(line1(:,1),line1(:,3)/8,'dr')
plot(f1,'--r')
plot(f11,'--k')
legend('No Phase Shift','With Phase Shift')
title('BaFeO_{3-\delta}')
ylim([-85.7, -84.2])
ylabel('DFT E(0K) (eV)')
xlabel('\delta')
hold off 

%% O chemical potential for old data

Ho = [];
So = [];
for t = 1:length(Temp)
    [H,S] = get_O2_thermo(Temp(t));
    Ho(t) = H;
    So(t) = S;
end
conv = 96.487;  % 1 eV = 96.487 kJ/mol
R_gas = 8.314462/1000/conv; %eV/K
Ho = transpose(Ho);
So = transpose(So);

muhg_o2 = Ho/conv - (Temp).*So/conv + R_gas.*(Temp).*log(Press);

muhg_o = 0.5*muhg_o2;

%% O chemical potential for new data

Ho_new = [];
So_new = [];
for t = 1:length(Temp_new)
    [H_new,S_new] = get_O2_thermo(Temp_new(t));
    Ho_new(t) = H;
    So_new(t) = S;
end
conv = 96.487;  % 1 eV = 96.487 kJ/mol
Ho_new = transpose(Ho_new);
So_new = transpose(So_new);

muhg_o2_new = Ho_new/conv - (Temp_new).*So_new/conv + R_gas.*(Temp_new).*log(Press_new);

muhg_o_new = 0.5*muhg_o2_new;

