figure;
colormap(jet);

% Create axes
axes1 = axes;
hold(axes1,'on');

% Create scatter3
scatter3(X1,Y1,Z1,S1,C1,'MarkerEdgeColor','none','AlphaDataMapping','none',...
    'MarkerFaceColor','flat');

% Create light
light('Parent',axes1,...
