box(axes1,'on');
grid(axes1,'on');
hold(axes1,'off');
% Set the remaining axes properties
set(axes1,'BoxStyle','full','FontSize',14,'FontWeight','bold','LineWidth',2,...
    'XMinorTick','on','YMinorTick','on');
